function calcEnergy(room) {
    if (room.energyCapacityAvailable >= 1000) {
        return 1000;
    }
    else if (room.energyCapacityAvailable >= 700) {
        return 700;
    }
    else if (room.energyCapacityAvailable >= 550) {
        return 550;
    }
    else if (room.energyCapacityAvailable >= 300) {
        return 300;
    }
}